    private static final String REGISTER_URL = "http://vso.manipal.edu/My_Profile.aspx";
    public static final String KEY_VIEWSTATE = "__VIEWSTATE";
    public static final String KEY_VIEWSTATEGENERATOR = "__VIEWSTATEGENERATOR";
    public static final String KEY_EVENTVALIDATION = "__EVENTVALIDATION";
    public static final String KEY_EMAIL = "ctl00$ContentPlaceHolder1$Email_My_Profile";
    public static final String KEY_DESIGNATION = "ctl00$ContentPlaceHolder1$Designation_DD";
    public static final String KEY_CONTACT = "ctl00$ContentPlaceHolder1$Contact_No_My_Profile";
    public static final String KEY_GENDER = "ctl00$ContentPlaceHolder1$Gender_My_Profile";
    public static final String KEY_BLOODGROUP = "ctl00$ContentPlaceHolder1$Blood_My_Profile";
    public static final String KEY_DOB = "ctl00$ContentPlaceHolder1$DOB_Tbx_My_Profile";
    public static final String KEY_NATIONALITY = "ctl00$ContentPlaceHolder1$Nationality_My_Profile";
    public static final String KEY_PERMADDRESS = "ctl00$ContentPlaceHolder1$Permanent_Address_My_Profile";
    public static final String KEY_PRESENTADDRESS = "ctl00$ContentPlaceHolder1$Present_Addtess_My_Profile";
    public static final String KEY_IMAGE = "ctl00$ContentPlaceHolder1$File_Upload_1";
    public static final String SAVE_CHANGES = "ctl00$ContentPlaceHolder1$SDave_Changes_My_Profile";
    public static final String KEY_INSTOTUTION = "ctl00$ContentPlaceHolder1$DropDownList_Institutions";
    public static final String KEY_REGNO = "ctl00$ContentPlaceHolder1$Reg_Emp_My_Profile";
    public static final String KEY_COURSE = "ctl00$ContentPlaceHolder1$DropDownList_Course";

    public static final String viewState = "";
    public static final String viewGenerator = "C22D402E";
    public static final String eventValidation = "";
    public static final String saveChanges = "Save Changes";
    //    public static final String campus = "MPL";
//    public static final String firstName = "Rakshit";
